model = genai.GenerativeModel(
  model_name="gemini-1.5-flash",
  generation_config=generation_config,
  safety_settings = safety_set,
  # See https://ai.google.dev/gemini-api/docs/safety-settings
  system_instruction=f"Eres un Bot asistente de la psicorientdaora del colegio María Dolorosa. Debes ser amable y dar respuestas concisas teniendo en cuenta el siguiente json sobre los casos atendidos en el año. En variable tipo, A significa Acudiante y E significa Estudiante, por tanto A-E significa que ambos estuvieron presentes en la consulta. Casos atendidos: {di}",
)

chat = model.start_chat(history=[])

st.title("Asistente ChatBot de Angela")




with st.expander("ℹ️ Descargo de responsabilidad"):
    st.caption(
        """Importante: La información proporcionada por esta aplicación es de carácter estrictamente informativo y no constituye un reporte oficial o legal."""
    )

if "messages" not in st.session_state:
    st.session_state.messages = []

if "max_messages" not in st.session_state:
    # Counting both user and assistant messages, so 10 rounds of conversation
    st.session_state.max_messages = 20

for message in st.session_state.messages:
    with st.chat_message(message["role"]):
        st.markdown(message["content"])

if len(st.session_state.messages) >= st.session_state.max_messages:
    st.info(
        """Máximo puedo ofrecer 10 respuestas. Reninicia la app en unos minutos para continuar."""
    )

else:
    if prompt := st.chat_input("¿Cúal es tu pregunta?"):
        st.session_state.messages.append({"role": "user", "content": prompt})
        with st.chat_message("user"):
            st.markdown(prompt)

        with st.chat_message("assistant"):
            try:
                response  = chat.send_message(prompt)
                st.markdown(response.text)
                st.session_state.messages.append(
                    {"role": "assistant", "content": response.text}
                )
            except:
                st.session_state.max_messages = len(st.session_state.messages)
                rate_limit_message = """
                    Oops! Perdón. Es mejor usarlo con moderación. Está en fase de prueba.
                """
                st.session_state.messages.append(
                    {"role": "assistant", "content": rate_limit_message}
                )
                st.rerun()